  const Understand = useRef(null);
  const Research = useRef(null);
  const Ideate = useRef(null);
  const Prototype = useRef(null);
  const Validate = useRef(null);

  //Other
  const projectsCount = projectData.length - 1;

  //Scroll to functions
  const scrollToBackground = () => {
    setActive(0);
    Background.current?.scrollIntoView({ behavior: "smooth" });
  };
  const scrollToUnderstand = () => {
    setActive(1);
    Understand.current?.scrollIntoView({ behavior: "smooth" });
  };
  const scrollToResearch = () => {
    setActive(2);
    Research.current?.scrollIntoView({ behavior: "smooth" });
  };
  const scrollToIdeate = () => {
    setActive(3);
    Ideate.current?.scrollIntoView({ behavior: "smooth" });
  };
  const scrollToPrototype = () => {
    setActive(4);
    Prototype.current?.scrollIntoView({ behavior: "smooth" });
  };
  const scrollToValidate = () => {
    setActive(5);
    Validate.current?.scrollIntoView({ behavior: "smooth" });
  };

  const [scrollPosition, setScrollPosition] = useState(0);
  
  const handleScroll = () => {
      const position = window.scrollY;
      if(position > 0 && position < Understand.current.offsetTop-100){
        setActive(0)
      }
      else if(position > Understand.current.offsetTop-100  && position < Research.current.offsetTop-100){
        setActive(1)
      }
      else if(position > Research.current.offsetTop-100 && position < Ideate.current.offsetTop-100){
        setActive(2)
      }
      else if(position > Ideate.current.offsetTop-100 && position < Prototype.current.offsetTop-100){
        setActive(3)
      }
      else if(position > Prototype.current.offsetTop-100 && position < Validate.current.offsetTop-100){
        setActive(4)
      }
      else if(position > Validate.current.offsetTop-100){
        setActive(5)
      }
      setScrollPosition(position);
      //console.log(position)
  };
  
  useEffect(() => {
      window.addEventListener('scroll', handleScroll, { passive: true });
  
      return () => {
          window.removeEventListener('scroll', handleScroll);
      };
  }, []);

